template< typename T >
std::string NumToHex(const T num, bool padd)
{
	std::stringstream stream;
	stream << std::setfill('0')
		<< std::setw(padd ? sizeof(T) * 2 : 0)
		<< std::hex << num;
	return stream.str();
}

std::string GenerateRandomNumber(const uint64_t bytes)
{
	std::string res("0x");
	const auto count = bytes / sizeof(uint32_t);
	for (auto i = 0; i < count; ++i)
	{
		res.append(NumToHex(rand(), true));
	}

	const size_t bitsInLast = bytes % sizeof(uint32_t);
	if (bitsInLast > 0)
	{
		const auto t = NumToHex(rand(), false);
		res.append(t.begin(), t.begin() + bitsInLast);
	}
	return res;
}

template <typename Base1, typename Mul1>
void Compare(const BigInt<Base1, Mul1>& v1, const std::string& h2)
{
	const auto h1 = v1.toHex();
	if (h1 != h2)
	{
		std::cout << "Error, Hex-values do not match:" << std::endl
			<< "First:  " << h1 << std::endl
			<< "Second: " << h2 << std::endl;
		throw std::logic_error("Invalid Hex-values");
	}
}

template <typename Base1, typename Mul1, typename Base2, typename Mul2>
void Compare(const BigInt<Base1, Mul1>& v1, const BigInt<Base2, Mul2>& v2)
{
	const auto h2 = v2.toHex();
	Compare(v1, h2);
}

void Test()
{
	BigInt<uint8_t, uint16_t> n("0xA01AFEDCBA01");
	BigInt<uint8_t, uint16_t> m("0x19F958");
	BigInt<uint8_t, uint16_t> s("0x80F958");

	BigInt<uint8_t, uint16_t> res("0x103E942EBA76E3E958");

	std::cout << (res / BigInt<uint8_t, uint16_t>((uint8_t)2)).toHex() << std::endl;
	std::cout << (res / BigInt<uint8_t, uint16_t>((uint8_t)4)).toHex() << std::endl;
	std::cout << (res / BigInt<uint8_t, uint16_t>((uint8_t)16)).toHex() << std::endl;


	auto a = (n << 2);
	std::cout << a.toHex() << std::endl;

	auto c = (s << 10);
	std::cout << c.toHex() << std::endl;

	Compare(n, (n << 1) >> 1);
	std::cout << n.toHex() << std::endl;
	std::cout << a.toHex() << std::endl;
	std::cout << (a >> 8).toHex() << std::endl;
	std::cout << ((a >> 8) >> 8).toHex() << std::endl;
	std::cout << (n >> 8).toHex() << std::endl;

	auto b = (n >> 17);
	std::cout << (n >> 17).toHex() << std::endl;
	std::cout << (n >> 16).toHex() << std::endl;
	std::cout << (n >> 9).toHex() << std::endl;

	n % m;
	std::cout << n.toHex() << std::endl;

	std::cout << (n - n).toHex() << std::endl;

	Compare(BigInt<uint8_t, uint16_t>("0xFFFFFFFF")
		* BigInt<uint8_t, uint16_t>("0xFF0001"),
		BigInt<uint8_t, uint16_t>("0xFF0000FF00FFFF"));

	Compare(BigInt<uint32_t, uint64_t>("0x100001")
		* BigInt<uint32_t, uint64_t>("0x8001"),
		BigInt<uint8_t, uint16_t>("0x800108001"));
	Compare(m * n, n * m);
	Compare(m * n, res);
}

void Test2()
{
	const BigInt<uint32_t, uint64_t> shifted32("0x841FA01A");
	const BigInt<uint32_t, uint64_t> shifted31("0x1083F4035");
	BigInt<uint8_t, uint16_t> t8("0x841FA01AFEDCBA01");
	BigInt<uint16_t, uint32_t> t16("0x841FA01AFEDCBA01");
	BigInt<uint32_t, uint64_t> t32("0x841FA01AFEDCBA01");
	Compare(t8, t16);
	Compare(t16, t32);
	Compare(t8, t32);

	Compare(t8 >> 32, shifted32);
	Compare(t8 >> 31, shifted31);
	Compare(t16 >> 32, shifted32);
	Compare(t16 >> 31, shifted31);
	Compare(t32 >> 32, shifted32);
	Compare(t32 >> 31, shifted31);

	for (uint32_t shift = 1; shift < 33; ++shift)
	{
		Compare(t8 >> shift, t16 >> shift);
		Compare(t16 >> shift, t32 >> shift);
		Compare(t8 >> shift, t32 >> shift);
	}

}

void Test_ModDiv()
{
	Compare(BigInt<uint8_t, uint16_t>("0x801") - BigInt<uint8_t, uint16_t>("0x2"), "0x7FF");
	{
		BigInt<uint8_t, uint16_t> numerator("0x800");
		BigInt<uint8_t, uint16_t> div("0x21");

		auto rem = numerator % div;
		auto quot = numerator / div;
		std::cout << quot.toHex() << std::endl;
		std::cout << rem.toHex() << std::endl;
	}

	{
		const BigInt<uint32_t, uint64_t> numerator("0x109F8F8841FA0197A08DE37ACBFE1DCB211A01");
		const BigInt<uint32_t, uint64_t> div("0x716AFC098");
		Compare(BigInt<uint32_t, uint64_t>("0x109F8F8841FA0197A08DE37ACBFE1DCB211A01") -
			BigInt<uint32_t, uint64_t>("0x0C7D9FFF01AFC0A7A08DE37ACBFE1DCB211A01"),
			"0x421EF89404A40F00000000000000000000000");

		auto rem = numerator % div;
		auto quot = numerator / div;
		std::cout << quot.toHex() << std::endl;
		std::cout << rem.toHex() << std::endl;
	}
}

template<typename B, typename M>
void TestMulDiv()
{
	uint64_t acc = rand() + 1;

	std::chrono::high_resolution_clock::time_point mulStart = std::chrono::high_resolution_clock::now();

	while (true)
	{
		uint64_t v(rand() + 1);

		uint64_t newAcc = 0;
		if (!msl::utilities::SafeMultiply(v, acc, newAcc))
		{
			break;
		}
		Compare(BigInt<B, M>(v) * BigInt<B, M>(acc), BigInt<B, M>(v * acc));
		acc = newAcc;
	}
	std::chrono::high_resolution_clock::time_point mulEnd = std::chrono::high_resolution_clock::now();
	auto mulDur = std::chrono::duration_cast<std::chrono::microseconds>(mulEnd - mulStart).count();

	std::chrono::high_resolution_clock::time_point divStart = std::chrono::high_resolution_clock::now();

	while (acc > 0)
	{
		uint64_t v(rand() + 1);
		Compare(BigInt<B, M>(acc) / BigInt<B, M>(v), BigInt<B, M>(acc / v));
		acc = acc / v;
	}
	std::chrono::high_resolution_clock::time_point divEnd = std::chrono::high_resolution_clock::now();
	auto divDur = std::chrono::duration_cast<std::chrono::microseconds>(divEnd - divStart).count();

	std::cout << "Mul took: " << mulDur << "us" << std::endl;
	std::cout << "div took: " << divDur << "us" << std::endl;
}

template<typename B, typename M>
void TestOperands(int64_t v1, int64_t v2)
{
	Compare(BigInt<B, M>(v1) * BigInt<B, M>(v2), BigInt<B, M>(v1 * v2));
	Compare(BigInt<B, M>(v2) * BigInt<B, M>(v1), BigInt<B, M>(v2 * v1));

	Compare(BigInt<B, M>(v1) - BigInt<B, M>(v2), BigInt<B, M>(v1 - v2));
	Compare(BigInt<B, M>(v2) - BigInt<B, M>(v1), BigInt<B, M>(v2 - v1));

	Compare(BigInt<B, M>(v1) + BigInt<B, M>(v2), BigInt<B, M>(v1 + v2));
	Compare(BigInt<B, M>(v2) + BigInt<B, M>(v1), BigInt<B, M>(v2 + v1));

	if (v1 == 0 && v2 == 0)
	{
		return;
	}
	else if (v2 == 0)
	{
		std::swap(v1, v2);
		Compare(BigInt<B, M>(v1) % BigInt<B, M>(v2), BigInt<B, M>(std::abs(v1 % v2)));
		Compare(BigInt<B, M>(v1) / BigInt<B, M>(v2), BigInt<B, M>(v1 / v2));
		return;
	}
	else if (v1 == 0)
	{
		Compare(BigInt<B, M>(v1) % BigInt<B, M>(v2), BigInt<B, M>(std::abs(v1 % v2)));
		Compare(BigInt<B, M>(v1) / BigInt<B, M>(v2), BigInt<B, M>(v1 / v2));
		return;
	}

	Compare(BigInt<B, M>(v1) % BigInt<B, M>(v2), BigInt<B, M>(std::abs(v1 % v2)));
	Compare(BigInt<B, M>(v2) % BigInt<B, M>(v1), BigInt<B, M>(std::abs(v2 % v1)));

	Compare(BigInt<B, M>(v1) / BigInt<B, M>(v2), BigInt<B, M>(v1 / v2));
	Compare(BigInt<B, M>(v2) / BigInt<B, M>(v1), BigInt<B, M>(v2 / v1));

}

void TestWithRand()
{
	union Gen
	{
		Gen()
		{
			t0 = rand() % (UINT8_MAX + 1);
			t1 = rand() % (UINT8_MAX + 1);
			t2 = rand() % (UINT8_MAX + 1);
			t3 = rand() % (UINT8_MAX + 1);
		}

		int value;
		struct
		{
			uint8_t t0;
			uint8_t t1;
			uint8_t t2;
			uint8_t t3;
		};
	};

	for (uint64_t i = 0; i < 100000; ++i)
	{
		int64_t v1 = Gen().value;
		int64_t v2 = Gen().value;

		TestOperands<uint8_t, uint16_t>(v1, v2);
		TestOperands<uint16_t, uint32_t>(v1, v2);
		TestOperands<uint32_t, uint64_t>(v1, v2);
	}
}

template<typename B, typename M>
void TestGCD()
{
	BigInt<B, M> t1("12131072439211271897323671531612440428472427633701410925634549312301964373042085619324197365322416866541017057361365214171711713797974299334871062829803541");
	BigInt<B, M> t2("12027524255478748885956220793734512128733387803682075433653899983955179850988797899869146900809131611153346817050832096022160146366346391812470987105415233");
	std::chrono::high_resolution_clock::time_point s = std::chrono::high_resolution_clock::now();

	uint64_t iters = 0;
	auto gcd = t1.GreatestCommonDivisor(t2, iters);

	std::chrono::high_resolution_clock::time_point e = std::chrono::high_resolution_clock::now();
	auto dur = std::chrono::duration_cast<std::chrono::microseconds>(e - s).count();

	std::cout << "Calculating GCD took: "
		<< dur << "us, with '"
		<< iters
		<< "' iterations" << std::endl;
	std::cout << gcd.toHex() << std::endl;
}

template<typename B, typename M>
void Test_RSA()
{
	const char* rawData = "Testing testing !";
	BigInt<B, M> data = BigInt<B, M>::FromRawData(rawData, strlen(rawData));
	const std::string fromRawData = data.ToRawData();

	BigInt<B, M> p("12131072439211271897323671531612440428472427633701410925634549312301964373042085619324197365322416866541017057361365214171711713797974299334871062829803541");
	BigInt<B, M> q("12027524255478748885956220793734512128733387803682075433653899983955179850988797899869146900809131611153346817050832096022160146366346391812470987105415233");

	BigInt<B, M> n = p * q;
	BigInt<B, M> t = (p - 1) * (q - 1);

	BigInt<B, M> e(65537);
	uint64_t iters = 0;
	while (n.GreatestCommonDivisor(e, iters) != 1)
	{
		e = e + 1;
		std::cout << "Booboo" << std::endl;
	}
	// public-key(e, n)
	BigInt<B, M> d = e.ModuloMultiplicativeInverse(t);

	/*
	Encryption:

		m^e mod n
		Decryption:

		c^d mod n
	*/
	std::chrono::high_resolution_clock::time_point encryption_start = std::chrono::high_resolution_clock::now();
	BigInt<B, M> encrypted = data.PowMod(e, n);
	std::chrono::high_resolution_clock::time_point encryption_end = std::chrono::high_resolution_clock::now();

	const std::string encryptedData = encrypted.ToRawData();

	std::chrono::high_resolution_clock::time_point decryption_start = std::chrono::high_resolution_clock::now();
	BigInt<B, M> decrypted = encrypted.PowMod(d, n);
	std::chrono::high_resolution_clock::time_point decryption_end = std::chrono::high_resolution_clock::now();

	auto encryption = std::chrono::duration_cast<std::chrono::milliseconds>(encryption_end - encryption_start).count();
	auto decryption = std::chrono::duration_cast<std::chrono::milliseconds>(decryption_end - decryption_start).count();

	std::cout << "Encryption took :" << encryption << "ms" << std::endl;
	std::cout << "Decryption took :" << decryption << "ms" << std::endl;

	const std::string decryptedData = decrypted.ToRawData();

	{
		// yeaah
		std::cout << "Successfully encrypted '" << rawData << "'"
			<< std::endl << "And decrypted it: '" << decryptedData << "'" << std::endl;
	}

	if (decryptedData != rawData)
	{
		std::cout << "Something went wrong, somewhere.." << std::endl;
	}
}

template<typename B, typename M>
void RSA_Small()
{
	BigInt<B, M> p(11);
	BigInt<B, M> q(13);

	BigInt<B, M> n = p * q;
	BigInt<B, M> t = (p - 1) * (q - 1);

	BigInt<B, M> e(7);
	uint64_t iters = 0;
	while (n.GreatestCommonDivisor(e, iters) != 1)
	{
		e = e + 1;
		std::cout << "Booboo" << std::endl;
	}
	// public-key(e, n)
	BigInt<B, M> d = e.ModuloMultiplicativeInverse(t);

	const char* rawData = "Testing testing !";
	BigInt<B, M> data = BigInt<B, M>::FromRawData(rawData, strlen(rawData));
	const std::string fromRawData = data.ToRawData();


	std::chrono::high_resolution_clock::time_point encryption_start = std::chrono::high_resolution_clock::now();
	BigInt<B, M> encrypted = data.PowMod(e, n);
	std::chrono::high_resolution_clock::time_point encryption_end = std::chrono::high_resolution_clock::now();

	const std::string encryptedData = encrypted.ToRawData();

	std::chrono::high_resolution_clock::time_point decryption_start = std::chrono::high_resolution_clock::now();
	BigInt<B, M> decrypted = encrypted.PowMod(d, n);
	std::chrono::high_resolution_clock::time_point decryption_end = std::chrono::high_resolution_clock::now();

	auto encryption = std::chrono::duration_cast<std::chrono::microseconds>(encryption_end - encryption_start).count();
	auto decryption = std::chrono::duration_cast<std::chrono::microseconds>(decryption_end - decryption_start).count();

	std::cout << "Encryption took :" << encryption << "us" << std::endl;
	std::cout << "Decryption took :" << decryption << "us" << std::endl;

	const std::string decryptedData = decrypted.ToRawData();

	if (decryptedData == rawData)
	{
		// yeaah
		std::cout << "Successfully encrypted '" << rawData << "'"
			<< std::endl << "And decrypted it: '" << decryptedData << "'" << std::endl;
	}
	else
	{
		std::cout << "Something went wrong, somewhere.." << std::endl;
	}
}

